changeSet.sql:
create table  if not exists dh_doctor_inspection_time
(
    id   bigint not null generated by default as identity (start with 1) constraint pk_doctor_inspection_time  primary key,
    created_by         varchar(255),
    created_date       timestamp,
    last_modified_date timestamp,
    last_modified_by   varchar(255),
    deleted            boolean default false,
    version            numeric default 0 not null,
    doctor_id        int,
    start_time       date,
    video_inspection boolean,
    chat_inspection  boolean,
    voice_inspection boolean
);

ALTER TABLE dh_doctor_inspection_time ADD CONSTRAINT fk_doctor_inspection_time_doctor_id FOREIGN KEY (doctor_id) REFERENCES dh_doctor(id);

