changeSet.sql:
create table if not exists dh_doctor_settings ( id bigint not null generated by default as identity (start with 1) constraint pk_doctor_settings primary key, created_by varchar(255), created_date timestamp, last_modified_date timestamp, last_modified_by varchar(255), deleted boolean default false, version numeric default 0 not null, doctor_id bigint, inspection_period_minute int, online boolean default false, video_inspection boolean default false, chat_inspection boolean default false, voice_inspection boolean default false );
ALTER TABLE dh_doctor_settings ADD CONSTRAINT fk_doctor_settings_doctor_id FOREIGN KEY (doctor_id) REFERENCES dh_doctor(id);
alter table dh_doctor drop column online;
alter table dh_doctor drop column voice_inspection;
alter table dh_doctor drop column video_inspection;
alter table dh_doctor drop column chat_inspection;
alter table dh_doctor drop column inspection_period_minute;
